mapper.select:
select * from orders

    user_id=#{userId}

        and status=#{status}


order by id desc

select * from orders


        and phone=#{phone}


        and number= #{number}


        and status= #{status}


        and order_time  >=  #{beginTime}


        and order_time  <=  #{endTime}


        and user_id= #{userId}


order by id desc
